mapper.select:
SELECT count(*)
FROM FUNDING_PRODUCT
WHERE REPLACE(fcate, ' ', '') = #{cate}

SELECT *
FROM member
WHERE mid=#{mid}

SELECT * FROM ( SELECT ROWNUM cnt, f.* FROM ( SELECT fa.* FROM FUNDING_APPLY fa
ORDER BY fid desc
ORDER BY fid asc
ORDER BY fstartdate asc, fid asc
) f ) WHERE cnt BETWEEN #{start} AND #{end}

SELECT *
FROM FUNDING_APPLY
WHERE fid=#{fid}

mapper.update:
UPDATE MEMBER
SET SELLERCK=1
WHERE mid=#{mid}

mapper.select:
SELECT * FROM ( SELECT ROWNUM cnt, f.* FROM ( SELECT fp.fid, fname, to_char(fp.FENDDATE, 'yyyy-mm-dd') fenddate, sum(fo_price) price, sum(fo_donation) donation, fp.fgoal FROM FUNDING_ORDER fo, FUNDING_PRODUCT fp WHERE fo.fid=fp.fid
AND to_char(fp.FENDDATE, 'yymmdd')<to_char(sysdate, 'yymmdd')
GROUP BY fp.fid, fp.fname, fp.fenddate, fp.fgoal
ORDER BY fp.FENDDATE desc
ORDER BY fp.FENDDATE asc
) f ) WHERE cnt BETWEEN #{start} AND #{end}

SELECT * 
FROM FUNDING_PRODUCT 
WHERE fid=#{fid}

SELECT *
FROM FUNDING_ORDER
WHERE fid=#{fid}

mapper.update:
UPDATE MEMBER
SET point=point+${fo_price}+${fo_donation}
WHERE mid=#{mid}

mapper.select:
SELECT fname 
FROM FUNDING_PRODUCT 
WHERE fid=#{fid}
