mapper.insert:

		SELECT FUNDING_BOARD_LIST_SEQ.NEXTVAL FROM DUAL

INSERT INTO FUNDING_BOARD_LIST(    BNO 
					     		 	, TITLE 
					      			, CONTENT 
					      			, WRITER )
              		   VALUES(    #{bno}
              		  			, #{title}
              		  			, #{content}
              		  			, #{writer} )

mapper.update:
   UPDATE FUNDING_BOARD_LIST
	  SET TITLE    =   #{title},
	   	  CONTENT  =   #{content}
	WHERE BNO = #{bno}

UPDATE FUNDING_BOARD_LIST SET
HIT = HIT+1
WHERE BNO = #{bno}
