mapper.select:
SELECT count(*) AS funding_cnt, count(CASE WHEN fo.FO_DONATION > 0 THEN 1 end) AS donation_cnt, m.POINT AS point FROM funding_order fo, MEMBER m WHERE m.mid = fo.mid GROUP BY fo.mid, point HAVING fo.mid = #{mid}
UNION ALL
SELECT 0 AS funding_cnt, 0 AS donation_cnt, m.POINT AS point FROM MEMBER m WHERE NOT EXISTS ( SELECT mid FROM funding_order WHERE mid = #{mid} ) AND mid = #{mid}

mapper.update:
update member set
	mpw = #{mpw}
WHERE mid = #{mid}

update member set
	maddress = #{maddress},
	maddress_detail = #{maddress_detail}
WHERE mid = #{mid}

UPDATE MEMBER SET 
	point = TO_NUMBER(point) + TO_NUMBER(#{point}) 
WHERE mid = #{mid}
